import User from "../models/UserModel";
import HttpStatus from "http-status";
import Response from "../utils/Response";

class NewUserDataChecker {
  static validateCredentials = async (req, res, next) => {
    const { email } = req.body;
    const result = await User.findOne({ email });

    if (result) {
      return Response.errorMessage(
        res,
        "Account associated with this email already exists",
        HttpStatus.CONFLICT
      );
    }
    next();
  };
}

export default NewUserDataChecker;

import httpStatus from "http-status";
import Response from "../utils/Response";

class CheckPassword {
  static checkPassword(req, res,next) {
    const { password, passwordConfirm } = req.body;

    if (password !== passwordConfirm) {
      return Response.errorMessage(
        res,
        "Your password should be similar!",
        httpStatus.BAD_REQUEST
      );
    }
    next();
  }
}

export default CheckPassword;

import HttpStatus from "http-status";
import User from "../models/UserModel";
import TokenAuthenticator from "../utils/TokenAuthenticator";
import Response from "../utils/Response";

const protectedRoute = async (req, res, next) => {
  try {
    const bearerToken = req.headers.authorization;
    const token = bearerToken.split(" ")[1];

    if (!token) {
      return Response.errorMessage(
        res,
        "No token found!",
        HttpStatus.NOT_FOUND
      );
    }

    const payload = TokenAuthenticator.decodeToken(token);
    const { name } = payload;
    if (name === "JsonWebTokenError") {
      return Response.errorMessage(
        res,
        "Unauthorized, invalid token",
        HttpStatus.UNAUTHORIZED
      );
    }

    if (name === "TokenExpiredError") {
      return Response.errorMessage(
        res,
        "Unauthorized, Token has expired signin again to get new token",
        HttpStatus.UNAUTHORIZED
      );
    }
    const validUser = await User.findById(payload._id);
    if (!validUser) {
      return Response.errorMessage(
        res,
        "You' re not authorized!",
        HttpStatus.UNAUTHORIZED
      );
    }
    req.user = payload;

    req.token = token;
    next();
  } catch (error) {
    return Response.errorMessage(
      res,
      "You can not proceed without setting a valid token",
      HttpStatus.INTERNAL_SERVER_ERROR
    );
  }
};
export default protectedRoute;
